import React from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';
import { ProcessSummary } from '../utils/parser';

interface CpuChartProps {
  data: ProcessSummary[];
}

export const CpuChart: React.FC<CpuChartProps> = ({ data }) => {
  return (
    <div className="h-96">
      <h2 className="text-xl font-semibold text-gray-900 mb-4">CPU Usage Over Time</h2>
      <ResponsiveContainer width="100%" height="100%">
        <LineChart
          data={data}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis
            dataKey="timestamp"
            tickFormatter={(timestamp) => new Date(timestamp).toLocaleTimeString()}
          />
          <YAxis
            label={{ value: 'CPU Usage (%)', angle: -90, position: 'insideLeft' }}
          />
          <Tooltip
            labelFormatter={(timestamp) => new Date(timestamp).toLocaleString()}
            formatter={(value: number) => [`${value.toFixed(2)}%`, 'CPU Usage']}
          />
          <Legend />
          <Line
            type="monotone"
            dataKey="cpuUsage"
            stroke="#8884d8"
            activeDot={{ r: 8 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};
