import React, { useState } from 'react';
import { FileUpload } from './components/FileUpload';
import { CpuChart } from './components/CpuChart';
import { HeapAllocChart } from './components/HeapAllocChart';
import { ThreadTable } from './components/ThreadTable';
import { ProcessSummary } from './utils/parser';

const App: React.FC = () => {
  const [data, setData] = useState<ProcessSummary[]>([]);
  const [selectedTimeRange, setSelectedTimeRange] = useState<[Date, Date] | undefined>();

  const handleDataParsed = (parsedData: ProcessSummary[]) => {
    setData(parsedData);
    setSelectedTimeRange(undefined);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">
            JVM Thread Snapshot Visualizer
          </h1>

          {data.length === 0 ? (
            <FileUpload onDataParsed={handleDataParsed} />
          ) : (
            <div className="space-y-8">
              <div className="bg-white shadow rounded-lg p-6">
                <CpuChart data={data} />
              </div>

              <div className="bg-white shadow rounded-lg p-6">
                <HeapAllocChart data={data} />
              </div>

              <div className="bg-white shadow rounded-lg p-6">
                <ThreadTable data={data} selectedTimeRange={selectedTimeRange} />
              </div>

              <div className="flex justify-end">
                <button
                  onClick={() => setData([])}
                  className="px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  Upload New File
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default App;
