import React from 'react';
import { ProcessSummary } from '../utils/parser.ts';

interface ThreadTableProps {
  data: ProcessSummary[];
  selectedTimeRange?: [Date, Date];
}

export const ThreadTable: React.FC<ThreadTableProps> = ({ data, selectedTimeRange }) => {
  const latestSnapshot = data[data.length - 1];

  return (
    <div>
      <h2 className="text-xl font-semibold text-gray-900 mb-4">Thread Details</h2>
      {latestSnapshot && (
        <div className="text-gray-600 mb-2">
          Snapshot Timestamp: {new Date(latestSnapshot.timestamp).toLocaleString()}
        </div>
      )}
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Thread ID
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Name
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                State
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                CPU Usage
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Allocation Rate
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {latestSnapshot?.threads.map((thread) => (
              <tr key={thread.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {thread.id}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {thread.name}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {thread.state}
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {thread.cpuUsage.toFixed(2)}%
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                  {(thread.allocRate / (1024 * 1024)).toFixed(2)} MB/s
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};
